function gererMenu(){const e=document.getElementById("btnMenu"),s=document.getElementsByClassName("header__mobile__nav");e.classList.contains("is-active")?(e.classList.remove("is-active"),s[0].classList.add("header__mobile__nav--ferme")):(e.classList.add("is-active"),s[0].classList.remove("header__mobile__nav--ferme"))}document.getElementById("btnMenu").addEventListener("click",gererMenu.bind(this));